
"""Minimal FastAPI implementation for SIRAJ self-contained deployment"""

class FastAPI:
    def __init__(self, title="SIRAJ API", version="1.0.0"):
        self.title = title
        self.version = version
        self.routes = []
        
    def get(self, path):
        def decorator(func):
            self.routes.append(("GET", path, func))
            return func
        return decorator
        
    def post(self, path):
        def decorator(func):
            self.routes.append(("POST", path, func))
            return func
        return decorator
        
    def add_middleware(self, middleware_class, **kwargs):
        pass  # Minimal implementation
        
class HTTPException(Exception):
    def __init__(self, status_code, detail=""):
        self.status_code = status_code
        self.detail = detail
        
class JSONResponse:
    def __init__(self, content, status_code=200):
        self.content = content
        self.status_code = status_code
        
class FileResponse:
    def __init__(self, path, status_code=200):
        self.path = path
        self.status_code = status_code
        
class StaticFiles:
    def __init__(self, directory, name="static"):
        self.directory = directory
        self.name = name
